from Crypto.Util.number import bytes_to_long, getPrime

flag = b'???'
assert len(flag) == 60

x,y,z = bytes_to_long(flag[:20]), bytes_to_long(flag[20:40]), bytes_to_long(flag[40:])
p = getPrime(256)
a1 = (x**2 + y**2 + z**2) % p
a2 = (x**3 + y**3 + z**3) % p
a3 = (x**4 + y**4 + z**4) % p

print(f'p = {p}')
print(f'a1 = {a1}')
print(f'a2 = {a2}')
print(f'a3 = {a3}')

